import React, { Component } from 'react'
import { FaBackward } from 'react-icons/fa'
import EmployeeService from '../employeeServices'

class ViewEmployees extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id: this.props.match.params.id,
            employee: {},

        }

    }

    componentDidMount(){
        EmployeeService.getEmployeeById(this.state.id).then( res =>
            {this.setState({employee: res.data});
            console.log(res.data);

        })
    }

    cancel(){
        this.props.history.push('/employees');
    }
    render() {
        return (
            <div>
                <div className = "card col-md-6 offset-md-3">
                    <h3 className = "text-center"> View Employee Details</h3>
                    <div className = "card-body" >
                    <div className = "row">
                            <label> ID: { this.state.employee._id }</label>

                        </div>
                        <div className = "row">
                            <label> First Name: { this.state.employee.fName }</label>

                        </div>
                        <div className = "row">
                            <label> Last Name: { this.state.employee.lName }</label>

                        </div>
                        <div className = "row">
                            <label> Email ID: { this.state.employee.email }</label>

                        </div>
                        <div  style={{float:"right"}}>
                        <button  className="btn btn-primary" onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}>Back</button>
                        </div>
                    </div>

                </div>
            </div>
        )
    }
}

export default ViewEmployees
