import React, {useState} from 'react';
import EmployeeService from '../employeeServices'
import { useHistory } from 'react-router-dom'
import { toast } from 'react-toastify';
import { FaSave, FaWindowClose } from 'react-icons/fa';


const initialState ={
            fName: '',
            lName: '',
            emailId: ''

};
const AddEmployee=()=>{
    const [state, setState]=useState(initialState);
    const{fName,lName,emailId}= state;



    const history=useHistory();
    const addEmployee = async(data)=>{

        const res=await EmployeeService.createEmployee(data);

        console.log(res.data);
        if(res.data==='Employee added!'){

       console.log(data);
        toast.success("Successfully add employee details");
        setTimeout(()=> history.push("/employees"),500);
        }
        else {

            console.log(res.data);
            toast.error("Please provide valid Email");
        }
    };




    const handleSubmit =(e) =>{
        e.preventDefault();
        if(!fName || !emailId || !lName){
            toast.error("Please fill all values")
        }
        else{
                addEmployee(state);
        }

    };

    const cancel=()=>{
        history.push('/employees');
    }


    const handleInputChange = (e) =>{
        let { name, value  }= e.target;
    setState({ ...state, [name]:value});
    }

    return (
        <div>
            <br></br>
               <div className = "container">
               <h2 className="text-center">Add New Employee</h2>
                    <div className = "row">
                        <div className = "card col-md-6 offset-md-3 offset-md-3">

                            <div className = "card-body">
                                <form onSubmit={handleSubmit}>

                                        <label htmlFor="fName"> First Name: </label>
                                        <input type="text" placeholder="First Name" name="fName" className="form-control" 
                                           value={fName} onChange={handleInputChange} required/>


                                        <label htmlFor="lName"> Last Name: </label>
                                        <input type="text" placeholder="Last Name" name="lName" className="form-control" 
                                             value={lastName} onChange={handleInputChange} required/>


                                        <label htmlFor="emailId"> Email Id: </label>
                                        <input type="email" placeholder="Email Address" name="emailId" className="form-control" 
                                             value={emailId} onChange={handleInputChange} required/>


                                    <button style={{margin:"10px"}} className="btn btn-success" onClick={handleSubmit}><FaSave/> Save</button>
                                    <button style={{margin:"10px"}} className="btn btn-danger" onClick={()=>{cancel()}} style={{marginLeft: "10px"}}><FaWindowClose/> Cancel</button>


                                    </form>
                            </div>
                        </div>
                    </div>

               </div>
        </div>
    );
}


export default AddEmployee;
