import React, { Component, useEffect, useState } from 'react'
import EmployeeService from '../employeeServices'
import {useHistory} from 'react-router-dom'



const EmployeeList = () =>{

    const [data,setData]=useState([]);
    const history=useHistory();

    useEffect(()=>{
        getEmployees();
    },[])


    const addEmployee=()=>{
        history.push("/addEmployee");
    }

    const onDeleteEmployee = async(id)=>{
        if(window.confirm("Confirm Delete")){
            const res= await EmployeeService.deleteEmployee(id);
            if(res.status===200){
                alert("Delete Success");
                getEmployees();
            }
        }
    };

    const getEmployees = async()=>{
        const res= await EmployeeService.getEmployees();
        if(res.status===200){
            setData(res.data);
        }
    };

    return (
        <div>
             <h2 className="text-center">Employees List</h2>
             <div>
                <button style={{width:"150px"}} onClick={ () => addEmployee()} className="btn btn-primary"> Add Employee</button>
             </div>
             <br></br>
             <div >
                    <div>

                        <thead>
                            <tr>
                                <th style={{width:"150px"}}>First Name</th>
                                <th style={{width:"150px"}}>Last Name</th>
                                <th style={{width:"400px"}}>Email</th>
                                <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            { data && data.map((e, i)=>{
                                return(
                                    <tr key = {i}>
                                         <td> {e.fName} </td>   
                                         <td> {e.lName}</td>
                                         <td> {e.email}</td>
                                         <td style={{width:"100px"}}>
                                            <div>
                                                <a href={`/updateEmployee/${e._id}`}>
                                                    <button style={{width:"80px"}} className="btn btn-primary">Update </button>
                                                </a>
                                            </div>
                                         </td>
                                         <td style={{width:"100px"}}>
                                            <div>  
                                                <button style={{width:"80px"}} onClick={ () => onDeleteEmployee(e._id)} className="btn btn-primary">Delete </button>
                                            </div>
                                         </td>
                                         <td> 
                                            <div>
                                                <a href={`/viewEmployee/${e._id}`}>
                                                    <button style={{width:"80px"}} className="btn btn-primary">View </button>
                                                </a>
                                            </div>
                                         </td>
                                    </tr>
                            )
                            })}

                        </tbody>
                    </div>

             </div>

        </div>
    );
}
export default EmployeeList;
