import React, { Component } from 'react'
import { FaSave, FaWindowClose } from 'react-icons/fa';
import { toast } from 'react-toastify';
import EmployeeService from '../employeeServices'

class UpdateEmployee extends Component {
    constructor(props) {
        super(props)

        this.state = {
            id: this.props.match.params.id,
            fName: '',
            lName: '',
            email: ''
        }
        this.changeFirstName = this.changeFirstName.bind(this);
        this.changeLastName = this.changeLastName.bind(this);
        this.update = this.updateEmployee.bind(this);
    }

    componentDidMount(){
        EmployeeService.getEmployeeById(this.state.id).then( (res) =>{
            let employee = res.data;
            this.setState({fName: employee.fName,
                lName: employee.lName,
                email : employee.email
            });
        });
    }

    updateEmployee = (e) => {
        e.preventDefault();
        let employee = {fName: this.state.fName, lName: this.state.lName, email: this.state.email};
        console.log('employee => ' + JSON.stringify(employee));
        console.log('id => ' + JSON.stringify(this.state.id));
        EmployeeService.updateEmployee(employee, this.state.id).then( res => {
            if(res.data==='Employee updated!'){

                console.log(employee);
                toast.success("Record successfully Updated");
                this.props.history.push('/employees');
                 }
                 else {

                     console.log(res.data);
                     toast.error("Please provide valid Email");
                 }

        });
    }

    changeFirstName= (event) => {
        this.setState({fName: event.target.value});
    }

    changeLastName= (event) => {
        this.setState({lName: event.target.value});
    }

    changeEmail= (event) => {
        this.setState({email: event.target.value});
    }

    cancel(){
        this.props.history.push('/employees');
    }

    render() {
        return (
            <div>
                <br></br>
                   <div className = "container">
                   <h2 className="text-center">Update Employee Details</h2>
                        <div className = "row">
                            <div className = "card col-md-6 offset-md-3 offset-md-3">

                                <div className = "card-body">
                                <form>
                                        <div className = "form-group">
                                            <label> First Name: </label>
                                            <input placeholder="First Name" name="fName" className="form-control" 
                                                value={this.state.fName} onChange={this.changeFirstName}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> Last Name: </label>
                                            <input placeholder="Last Name" name="lName" className="form-control" 
                                                value={this.state.lName} onChange={this.changeLastName}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> Email Id: </label>
                                            <input placeholder="Email Address" name="email" className="form-control" 
                                                value={this.state.email} onChange={this.changeEmail}/>
                                        </div>

                                        <button style={{margin:"10px"}} className="btn btn-success" onClick={this.updateEmployee}><FaSave/> Save</button>
                                        <button style={{margin:"10px"}} className="btn btn-danger" onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}><FaWindowClose/> Cancel</button>
                                    </form>
                                </div>
                            </div>
                        </div>

                   </div>
            </div>
        )
    }
}

export default UpdateEmployee;
